<template>
	<h1>Lista de produtos</h1>
	<product-details :details="details"></product-details>

	<v-card max-width="400" class="mx-auto">
		<v-container>
			<v-row dense>
				<v-col cols="12" v-for="product in products" :key="product.id">
					<product
						@addCart="addCart"
						@showDetails="showDetails"
						:product="product"
					>
					</product>
				</v-col>
			</v-row>
		</v-container>
	</v-card>
</template>
<script>
import Product from "@/components/Product.vue";
import ProductDetails from "@/components/ProductDetails.vue";
import { ref } from "vue";

export default {
	emits: ["addCart"],
	setup(props, { emit }) {
		const details = ref({});
		const products = ref([
			{
				desc: "BANANA",
				price: "10.00",
				img: "https://www.infoescola.com/wp-content/uploads/2010/04/banana_600797891.jpg",
				id: 1,
			},
			{
				desc: "MAÇÃ",
				price: "5.00",
				img: "data:image/jpeg;base64,",
				id: 2,
			},
			{
				desc: "PERA",
				price: "1.00",
				img: "data:image/jpeg;base64,",
				id: 3,
			},
			{
				desc: "OVO",
				price: "10.00",
				img: "data:image/jpeg;base64,",
				id: 4,
			},
			{
				desc: "LIMÃO",
				price: "5.00",
				img: "data:image/jpeg;base64,",
				id: 5,
			},
		]);

		const addCart = (prod) => {
			emit("addCart", prod);
		};
		const showDetails = (prod) => {
			details.value = prod;
		};

		return { products, details, addCart, showDetails };
	},
	components: { Product, ProductDetails },
};
</script>
